mapper.select:
SELECT member_id, social_id, nickname, profile_image, solve_count, score, baekjoon_id, member_status, created_at, updated_at
FROM member
WHERE member_id = #{id}

SELECT member_id, social_id, nickname, profile_image, solve_count, score, baekjoon_id, member_status, created_at, updated_at
FROM member as m
WHERE m.social_id = #{socialId}

SELECT COUNT(*) > 0
FROM member as m
WHERE m.nickname = #{nickname}

mapper.update:
UPDATE member
SET nickname = #{nickname}, profile_image = #{profileImage}, baekjoon_id = #{baekjoonId}, member_status = #{memberStatus}
WHERE member_id = #{memberId}
